func newSimulatedBackend() (*backends.SimulatedBackend, error) {
	balance := big.NewInt(2e15)
	genesis := &core.Genesis{
		Config: params.AllEthashProtocolChanges,
		Alloc: core.GenesisAlloc{
			starknetAddress: {
				Balance: balance,
				Code:    []byte(contracts.StarknetMetaData.Bin),
			},
			verifierAddress: {
				Balance: balance,
				Code:    []byte(contracts.GpsStatementVerifierMetaData.Bin),
			},
			pageRegistryAddress: {
				Balance: balance,
				Code:    []byte(contracts.MemoryPageFactRegistryMetaData.Bin),
			},
			operatorAddress: {
				Balance: balance,
			},
		},
		Timestamp: 9000,
		BaseFee:   big.NewInt(params.InitialBaseFee),
	}
	database := rawdb.NewMemoryDatabase()
	backend := backends.NewSimulatedBackendWithDatabase(database, genesis.Alloc, 100000)
	generate := func(blockNum int, blockGen *core.BlockGen) {
		switch blockNum {
		case 0:
			blockGen.AddTx(registerProofAndVerifyTx)
		}
	}
	blocks, receipts := core.GenerateChain(backend.Blockchain().Config(), backend.Blockchain().Genesis(), backend.Blockchain().Engine(), database, 1, generate)
	headers := make([]*types.Header, len(blocks))
	for i, block := range blocks {
		headers[i] = block.Header()
	}
	if _, err := backend.Blockchain().InsertHeaderChain(headers, 1); err != nil {
		return nil, fmt.Errorf("failed to insert headers: %w", err)
	}
	if _, err := backend.Blockchain().InsertReceiptChain(blocks, receipts, 0); err != nil {
		return nil, fmt.Errorf("failed to insert receipts: %w", err)
	}
	return backend, nil
}

func newTestL1SyncService(t *testing.T) *L1SyncService {
	env, err := db.NewMDBXEnv(t.TempDir(), 2, 0)
	if err != nil {
		t.Fatal(err)
	}
	syncDatabase, err := db.NewMDBXDatabase(env, "SYNC")
	if err != nil {
		t.Fatal(err)
	}
	stateDatabase, err := db.NewMDBXDatabase(env, "STATE")
	if err != nil {
		t.Fatal(err)
	}

	backend, err := newSimulatedBackend()
	if err != nil {
		t.Fatalf("unexpected error while creating backend: %s", err)
	}

	chainConfig := L1ChainConfig{
		starknet: ContractConfig{
			address:         starknetAddress,
			deploymentBlock: 0,
		},
		gpsStatementVerifier: ContractConfig{
			address:         verifierAddress,
			deploymentBlock: 0,
		},
		memoryPageFactRegistry: ContractConfig{
			address:         pageRegistryAddress,
			deploymentBlock: 0,
		},
	}
	l1, err := NewL1SyncService(chainConfig, backend, syncdb.NewManager(syncDatabase), statedb.NewManager(stateDatabase, nil))
	if err != nil {
		t.Fatal(err)
	}

	return l1
}

func TestFindLogMemoryPagesHashes(t *testing.T) {
	l1 := newTestL1SyncService(t)

	gotPagesHashes, _, err := l1.findLogMemoryPagesHashes(1, factHash, 1)
	if err != nil {
		t.Fatalf("unexpected error while finding LogMemoryPagesHashes events: %s", err)
	}

	pagesHashesBytes := make([][32]byte, len(pagesHashes))
	for i, hash := range pagesHashes {
		pagesHashesBytes[i] = *(*[32]byte)(hash.Bytes())
	}

	assert.DeepEqual(t, gotPagesHashes, pagesHashesBytes)
}
